//!---------------------------------------------------- Fifth -------------------------------------------------------
$(".footer__main-fifth-content-rightlinktext").click(function () {
  let moretext = $(".footer__main-fifth-content-righttext");

  if (moretext.css("display") === "flex") {
    moretext.css("display", "-webkit-box");
    $(this).text("مشاهده بیشتر");
  } else {
    moretext.css("display", "flex");
    $(this).text("بستن");
  }
});
